package maosheji.services.front.favorite.bean;import java.io.Serializable;import maosheji.core.dao.page.PagerModel;import maosheji.services.front.product.bean.Product;public class Favorite extends maosheji.services.common.Favorite implements Serializable {	private static final long serialVersionUID = 1L;	private Product product;	public void clear() {		super.clear();		if(product!=null){			product.clear();		}	}	public Product getProduct() {		return product;	}	public void setProduct(Product product) {		this.product = product;	}	}