package maosheji.services.front.favorite.impl;import maosheji.core.ServersManager;import maosheji.services.front.favorite.FavoriteService;import maosheji.services.front.favorite.bean.Favorite;import maosheji.services.front.favorite.dao.FavoriteDao;public class FavoriteServiceImpl extends ServersManager<Favorite> implements		FavoriteService {	private FavoriteDao favoriteDao;	public void setFavoriteDao(FavoriteDao favoriteDao) {		this.favoriteDao = favoriteDao;	}	@Override	public int selectCount(Favorite favorite) {		return favoriteDao.selectCount(favorite);	}}