package maosheji.services.manage.orderdetail.impl;import java.util.List;import maosheji.core.ServersManager;import maosheji.services.manage.order.bean.Order;import maosheji.services.manage.orderdetail.OrderdetailService;import maosheji.services.manage.orderdetail.bean.Orderdetail;import maosheji.services.manage.orderdetail.dao.OrderdetailDao;import maosheji.web.action.manage.report.ReportInfo;public class OrderdetailServiceImpl extends ServersManager<Orderdetail>		implements OrderdetailService {	private OrderdetailDao orderdetailDao;	public void setOrderdetailDao(OrderdetailDao orderdetailDao) {		this.orderdetailDao = orderdetailDao;	}	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return orderdetailDao.reportProductSales(orderdetail);	}}